Handlebars.registerHelper('1timeconvert', function(timestamp, options) {
    var date = new Date(timestamp * 1000);
    var mes = date.getMonth() + 1;

    switch (mes) {
        case 1:
            month = "Enero";
            break;
        case 2:
            month = "Febrero";
            break;
        case 3:
            month = "Marzo";
            break;
        case 4:
            month = "Abril";
            break;
        case 5:
            month = "Mayo";
            break;
        case 6:
            month = "Junio";
            break;
        case 7:
            month = "Julio";
            break;
        case 8:
            month = "Agosto";
            break;
        case 9:
            month = "Septiembre";
            break;
        case 10:
            month = "Octubre";
            break;
        case 11:
            month = "Noviembre";
            break;
        case 12:
            month = "Diciembre";
            break;
        default:
            month = null;
            break;
    }

    /*var date = new Date(timestamp * 1000);
     var mes = date.getMonth() + 1; */
    var dia = date.getDate();
    var anyo = date.getFullYear();

    var minutos = date.getMinutes();
    minutos = (minutos < 10) ? "0" + minutos : minutos;
    return dia + " de " + month + " de " + anyo;

    return date;
});

